    if (this.web.valid) {
      this.link.setValue(this.web.value);
      this.linkTipo.setValue(this.linkSeleccionado);
      this.activarBtnGuardar();
    } else {
      this.link.setValue(null);
      this.linkTipo.setValue(null);
      this.activarBtnGuardar();
    }
  }

