export class AgregarComponent {
  constructor(cdRef, fb, vs, _snackBar, activatedRoute, router, dialog, configService, dormirService, mapaService) {
    this.cdRef = cdRef;
    this.fb = fb;
    this.vs = vs;
    this._snackBar = _snackBar;
    this.activatedRoute = activatedRoute;
    this.router = router;
    this.dialog = dialog;
    this.configService = configService;
    this.dormirService = dormirService;
    this.mapaService = mapaService;
    this.allowedSizeGallery = 150; //kilo bytes

    this.allowedSizeHome = 80; //kilo bytes

    this.cambiosConfirmados = false;
    this.departamentos = [];
    this.directorio = ''; //subcarpeta con el nombre del lugar

    this.directorioPadre = 'donde_dormir'; //carpeta raíz donde se almacenan los lugares

    this.heightAllowedEvento = 150;
    this.localidades = [];
    this.titulo = "Nuevo Hotel";
    this.widthAllowedEvento = 150;
    this.hoteles = [];
    this.prioridades = [];
    this.imagenDefault = {
      "name": "imagen-default",
      "url": "assets/default-lugar-galeria.jpg"
    };
    this.imagenRestaurante = this.imagenDefault;
    this.mapaTouched = false;
    this.imagenesBorradas = []; // solo guarda las imagenes que se eliminaron y no se guardo el formulario

    this.disabledAddPhones = false;
    this.unsubscribe$ = new Subject();
    this.hotelForm = this.fb.group({
      carpeta: [null],
      departamento: ['', Validators.required],
      direccion: [''],
      imagen: [this.imagenDefault],
      localidad: ['', [Validators.required]],
      nombre: ['', [Validators.required, Validators.minLength(3), Validators.maxLength(50)]],
      publicado: [false],
      telefonos: this.fb.array([this.fb.group({
        numero: ['', [this.vs.validarTelefono]]
      })]),
      ubicacion: [null, [Validators.required, this.vs.validarUbicacion]]
    });
    this.latitud = this.fb.control(null, [this.vs.validarLatitud]);
    this.longitud = this.fb.control(null, [this.vs.validarLongitud]);
  }

  ngOnInit() {
    this.configService.getObsDepartamentos().pipe(takeUntil(this.unsubscribe$)).subscribe(dptos => this.departamentos = dptos);
    this.configService.getObsLocalidades().pipe(takeUntil(this.unsubscribe$)).subscribe(locs => this.localidades = locs);
    this.dormirService.getObsHoteles$().pipe(takeUntil(this.unsubscribe$)).subscribe(hoteles => this.hoteles = hoteles);
    this.configService.emitirDepartamentosActivos();
    this.configService.emitirLocalidades();
    /** Observable que se dispara al cambiar el valor del minimapa.
    *  Los datos del formulario cambian en funcion del valor del mimimapa
    */

    this.mapaService.getObsMiniMapa().pipe(takeUntil(this.unsubscribe$)).subscribe(res => {
      //si los datos del minimapa son validos y tiene marcado en true
      if (res !== undefined && res.marcador === true) {
        this.ubicacion.setValue(res.centro);
      } else if (res.marcador === false) {
        this.ubicacion.setValue(null);
      }

      console.log(JSON.stringify(res));
    });
    /**
    * A partir de la ruta y el id recibido obtiene el lugar para mostrar
    */

    this.activatedRoute.params.pipe(switchMap(({
      id
    }) => this.dormirService.geHotelId(id)) //    tap(res => console.log(res))
    ).subscribe(hotel => {
      let restoranActual = JSON.parse(JSON.stringify(hotel));

      if (restoranActual.id !== undefined) {
        //Si estamos editando un lugar
        this.idRestoran = restoranActual.id;
        delete restoranActual.id; //para setear el formulario es necesario quitar el tatributo id

        for (let i = 0; i < restoranActual.telefonos.length; i++) {
          if (i > 0) {
            this.agregarNuevoTelefonoAlFormulario();
          }
        }

        this.hotelForm.reset(restoranActual);
        this.titulo = `Editando ${this.hotelForm.controls['nombre'].value}`;
        this.latitud.setValue(this.ubicacion.value.lat);
        this.longitud.setValue(this.ubicacion.value.lng);
        this.imagenRestaurante = restoranActual.imagen;
        this.directorio = this.carpeta.value;
        this.configService.getLocadidadesDepartamento(restoranActual.departamento);
        this.mapaService.dMiniMapa = {
          centro: restoranActual.ubicacion,
          zoom: 15,
          marcador: true
        };
        this.mapaService.emitirMiniMapa();
      } else {}
    }); // Si es un hotel nuevo crear un nombre para la carpeta

    if (this.carpeta.value === null) {
      this.directorio = this.dormirService.randomString(7);
      this.carpeta.setValue(this.directorio);
    }
  }

  ngAfterViewInit() {
    //es para que no se vea el error de cambio
    this.cdRef.detectChanges();
  }

  OnDestroy() {
    // this.sourceDepartamentos.unsubscribe();
    // this.sourceLocalidades.unsubscribe();
    this.unsubscribe$.next();
    this.unsubscribe$.complete(); //limpia el mapa y el mini-mapa

    this.mapaService.resetDataMapa();
    this.mapaService.resetDataMiniMapa(); //Limpia el minimapa y el mapa

    this.mapaService.emitirMiniMapa();
    this.mapaService.resetDataMapa();
  }
  /**
   * Abre el dialog con para el mapa
   */


  abrirMapa() {
    this.mapaTouched = true;
    const dialogConfig = new MatDialogConfig();
    dialogConfig.disableClose = false;
    dialogConfig.autoFocus = true;
    dialogConfig.minHeight = "565px"; //dialogConfig.minHeight = "450px";
    //dialogConfig.minWidth = "900px";

    dialogConfig.minWidth = "1400px";
    dialogConfig.id = "dialogMapa";
    dialogConfig.data = 0;
    this.dialog.open(DialogMapaComponent, dialogConfig);
  }
  /**
   * Función para prevenir la publicación de un lugar sin los datos indispensables
   * para la app
   */


  switchPublicar() {
    let test = true;
    let _imagen = "OK";
    let _ubicacion = "OK";
    let _descripcion = "OK";

    if (this.imagen.value.url === "assets/default-lugar-galeria.jpg") {
      test = false;
      this.publicado.setValue(false);
      _imagen = "No válida";
    }

    if (!test) {
      this.dialog.open(DialogPublicarComponent, {
        data: {
          imagen: _imagen,
          ubicacion: _ubicacion,
          descripcion: _descripcion
        }
      });
    }
  }

  regresar() {
    this.router.navigate(['/donde-dormir/listado']);
  }
  /**
   * Al cambiar la imagen del evento, asigna el valor recibido a  la variable imagenEvento del formulario.
   * @param $event - Contiene los datos de la imágen subida para el evento.
   * Los datos son la url y el nombre del archivo.
   */


  setImagenRestaurante($event) {
    if ($event != "") {
      this.imagenRestaurante = $event;
    } else {
      this.imagenRestaurante = this.imagenDefault;
    }
  }

  setUbicacionManual() {
    if (this.longitud.valid && this.latitud.valid) {
      this.ubicacion.setValue({
        "lng": this.longitud.value,
        "lat": this.latitud.value
      });
    }
  }

  quitarEspacios() {}
  /** No se está usando ahora
   * Función para mostrar un mensaje corto al usuario
   * @param message Es el texto que se muestra en el snackBar
   */


  openSnackBarSubmit(message) {
    this._snackBar.open(message, "Aceptar", {
      duration: 5000
    });
  }
  /**
   * Este método es llamado cada vez que se selecciona un departamento
   */


  getLocalidadesPorDepartamento() {
    this.configService.getLocadidadesDepartamento(this.departamento.value);
  }
  /** Enviar en formulario a firebase */


  submitEvento() {
    var _this = this;

    return _asyncToGenerator(function* () {
      if (_this.publicado.value === true) {
        _this.switchPublicar();
      } //envia el formulario


      _this.hotelForm.controls['imagen'].setValue(_this.imagenRestaurante);

      if (_this.hotelForm.valid) {
        _this.cambiosConfirmados = true; //verifica si es una actualización o un hotel nuevo

        if (_this.idRestoran !== undefined) {
          //si se esta editando un hotel
          const restauran = _this.hotelForm.value;
          restauran.id = _this.idRestoran;

          _this.dormirService.updateHotelLocal(restauran);

          _this.dormirService.updateHotelFirestore(_this.hotelForm.value, _this.idRestoran).then(res => {
            _this.openSnackBarSubmit('¡El hotel se ha actualizado correctamente!');
          }).catch(error => {
            _this.openSnackBarSubmit('¡Error, no se ha podido actualizar el hotel en Firestore!');

            console.error('¡Error, no se ha podido actualizar el Resturante en Firestore!. Error: ' + error);
          }); //limpia el mapa y el mini-mapa


          _this.mapaService.resetDataMapa();

          _this.mapaService.resetDataMiniMapa(); //Limpia el minimapa y el mapa


          _this.mapaService.emitirMiniMapa();

          _this.mapaService.resetDataMapa();

          _this.regresar();
        } else {
          //Si el Hotel es nuevo
          let nuevoId;

          _this.dormirService.addHotel(_this.hotelForm.value).then(id => {
            nuevoId = id;

            if (nuevoId !== '' && nuevoId !== undefined) {
              _this.openSnackBarSubmit('¡El nuevo hotel se ha guardado correctamente con el ID: ' + nuevoId);
            }
          }).catch(error => {
            console.log(error);

            _this.openSnackBarSubmit('¡Por algún motivo el nuevo hotel no se pudo guardar!');
          }); //limpia el mapa y el mini-mapa


          _this.mapaService.resetDataMapa();

          _this.mapaService.resetDataMiniMapa(); //Limpia el minimapa y el mapa


          _this.mapaService.emitirMiniMapa();

          _this.mapaService.resetDataMapa(); //limpia el formulario y setea los valores inicales con el metodo reset


          _this.hotelForm.reset({
            imagen: _this.imagenDefault
          });

          _this.regresar();
        }
      }
    })();
  }
  /**
   * Función que agrega un nuevo formControl de tipo telefono
   */


  agregarNuevoTelefonoAlFormulario() {
    const telefonosControl = this.hotelForm.get('telefonos');
    telefonosControl.push(this.fb.group({
      numero: ['', [this.vs.validarTelefono]]
    }));
    this.disabledAddPhones = telefonosControl.length >= 2 ? true : false;
  }

  eliminarTelefonoDelFormulario(i) {
    const telefonosControl = this.hotelForm.get('telefonos');
    telefonosControl.removeAt(i);
    this.disabledAddPhones = telefonosControl.length >= 2 ? true : false;
  } // Getters


  get carpeta() {
    return this.hotelForm.get('carpeta');
  }

  get departamento() {
    return this.hotelForm.get('departamento');
  }

  get direccion() {
    return this.hotelForm.get('direccion');
  }

  get imagen() {
    return this.hotelForm.get('imagen');
  }

  get localidad() {
    return this.hotelForm.get('localidad');
  }

  get nombre() {
    return this.hotelForm.get('nombre');
  }

  get publicado() {
    return this.hotelForm.get('publicado');
  }

  get telefonos() {
    return this.hotelForm.get('telefonos');
  }

  get ubicacion() {
    return this.hotelForm.get('ubicacion');
  }

